import { defineStore } from 'pinia';
// project imports
import axios from '@/utils/axios';
import type { Reply } from '@/types/apps/PostType';

export const usePostsStore = defineStore({
  id: 'post',
  state: () => ({
    posts: [],
  }),
  getters: {},
  actions: {
    // Fetch Posts from action
    async fetchPosts() {
      try {
        const response = await axios.get('/api/posts/list');
        this.posts = response.data;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },
    // like post
    async likePost(postId: string) {
      try {
        const response = await axios.post('/api/posts/list/like', { postId });
        this.posts = response.data.posts;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },
    // add Comment
    async addComment(postId: string, comment: Reply) {
      try {
        const response = await axios.post('/api/comments/add', { postId, comment });
        this.posts = response.data.posts;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },
    // add Comment
    async addReply(postId: string, commentId: string, reply: Reply) {
      try {
        const response = await axios.post('/api/replies/add', { postId, commentId, reply });
        this.posts = response.data.posts;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },
  },
});
<script setup lang="ts">
import { ref } from 'vue';
import { usePostsStore } from '@/stores/apps/userprofile/posts';
import uniqueId from 'lodash/uniqueId';

const store = usePostsStore();
const props = defineProps({
  comments: Object || Array,
  postId: String,
});
const replyValue = ref('');
const onSubmit = async (id: any, commentid: any, reply: string) => {
  const replyId = uniqueId('#REPLY_');
  const newReply = {
    id: replyId,
    profile: {
      id: uniqueId('#REPLY_'),
      avatar: props.comments?.profile.avatar,
      name: props.comments?.profile.name,
      time: 'now',
    },
    data: {
      comment: reply,
      likes: {
        like: false,
        value: 0,
      },
      replies: [],
    },
  };
  store.addReply(id, commentid, newReply);
  replyValue.value = '';
};
const showReplyBox = ref(false);
const toggleReplybox = () => {
  return (showReplyBox.value = !showReplyBox.value);
};
</script>

<template>
  <v-card variant="flat" class="mb-3 pa-5 border bg-surface">
    <div class="d-flex gap-3 align-center">
      <v-avatar size="30">
        <img :src="comments?.profile.avatar" width="30" alt="avatar" />
      </v-avatar>
      <div class="d-block d-sm-flex align-center gap-3">
        <h6 class="text-h6">{{ comments?.profile.name }}</h6>
        <span class="text-subtitle-2 opacity-50">
          <CircleIcon size="8" fill="inherit" class="opacity-50 mr-1" />
          {{ comments?.profile.time }}
        </span>
      </div>
    </div>
    <div class="py-3 text-body-1">
      {{ comments?.data.comment }}
    </div>
    <!---Like and comment count-->
    <div class="my-1 d-flex align-center gap-3">
      <v-tooltip text="Like">
        <template v-slot:activator="{ props }">
          <v-btn icon color="primary" variant="flat" size="x-small" v-bind="props">
            <ThumbUpIcon size="16" />
          </v-btn>
        </template>
      </v-tooltip>
      <span class="text-subtitle-1 font-weight-semibold">{{
        comments?.data && comments?.data.likes && comments?.data.likes.value
      }}</span>
      <v-tooltip text="Reply">
        <template v-slot:activator="{ props }">
          <v-btn
            icon
            color="secondary"
            variant="flat"
            v-bind="props"
            size="x-small"
            @click="toggleReplybox"
          >
            <ArrowBackUpIcon size="16" />
          </v-btn>
        </template>
      </v-tooltip>
    </div>
  </v-card>

  <div v-if="comments?.data.replies" class="ml-10">
    <div v-for="reply in comments?.data.replies">
      <v-card variant="flat" class="mb-3 pa-5 border">
        <div class="d-flex gap-3 align-center">
          <v-avatar size="30">
            <img :src="reply.profile.avatar" width="30" alt="avatar" />
          </v-avatar>
          <div class="d-block d-sm-flex align-center gap-3">
            <h6 class="text-h6">{{ reply.profile.name }}</h6>
            <span class="text-subtitle-2 opacity-50">
              <CircleIcon size="8" fill="inherit" class="opacity-50 mr-1" />
              {{ reply.profile.time }}
            </span>
          </div>
        </div>
        <div class="py-3 text-body-1">
          {{ reply.data.comment }}
        </div>
        <!---Like and comment count-->
        <!-- <div class="my-1 d-flex gap-3">
                    <v-btn color="primary" variant="text" size="small" prepend-icon="mdi-thumb-up-outline">
                        {{ reply.data && reply.data.likes && reply.data.likes.value }}
                    </v-btn>
                    <v-btn color="secondary" variant="text" size="small" prepend-icon="mdi-reply" @click="toggleReplybox"> Reply </v-btn>
                </div> -->
      </v-card>
    </div>
  </div>
  <div class="d-block d-sm-flex gap-3 align-center mb-5 ml-10" v-if="showReplyBox">
    <img
      :src="comments?.profile.avatar"
      width="40"
      class="flex-shrink-0 d-none d-sm-block"
      alt="avatar"
    />
    <v-text-field
      variant="outlined"
      color="primary"
      v-model="replyValue"
      label="Write Reply"
      hide-details
    ></v-text-field>
    <v-btn
      color="secondary"
      class="mt-3 mt-sm-0"
      variant="flat"
      size="large"
      @click="onSubmit(postId, comments?.id, replyValue)"
    >
      Reply
    </v-btn>
  </div>
</template>

<style lang="scss" scoped>
.border-light {
  border: 1px solid rgb(var(--v-theme-grey100));
}
</style>
<script setup lang="ts">
import { ref } from 'vue';
import { usePostsStore } from '@/stores/apps/userprofile/posts';
import Comments from './Comments.vue';
import uniqueId from 'lodash/uniqueId';
import { ShareIcon, UsersIcon, Message2Icon, ClipboardIcon } from 'vue-tabler-icons';

const store = usePostsStore();
const props = defineProps({
  post: Object || Array,
});

const showCommentBox = ref(!(props.post?.comments && props.post?.comments.length > 0));
const searchValue = ref('');
const shareitems = ref([
  { title: 'Share Now', icon: ShareIcon },
  { title: 'Share to Friends', icon: UsersIcon },
  { title: 'Send in Messanger', icon: Message2Icon },
  { title: 'Copy Link', icon: ClipboardIcon },
]);

const toggleCommentbox = () => {
  return (showCommentBox.value = !showCommentBox.value);
};

const handlePostLikes = async (postId: string) => {
  await store.likePost(postId);
};
const onSubmit = async (id: any, comment: any) => {
  const commentId = uniqueId('#COMMENT_');
  const newComment: any = {
    id: commentId,
    profile: {
      id: uniqueId('#COMMENT_'),
      avatar: props.post?.profile.avatar,
      name: props.post?.profile.name,
      time: 'now',
    },
    data: {
      comment: comment,
      likes: {
        like: false,
        value: 0,
      },
      replies: [],
    },
  };
  store.addComment(id, newComment);
  searchValue.value = '';
};
</script>

<template>
  <v-card elevation="10" class="bg-surface">
    <v-card-item>
      <div class="d-flex gap-3 align-center">
        <v-avatar size="40">
          <img :src="post?.profile.avatar" width="40" alt="avatar" />
        </v-avatar>
        <div class="d-block d-sm-flex align-center gap-3">
          <h6 class="text-h6">{{ post?.profile.name }}</h6>
          <span class="text-subtitle-2 opacity-50">
            <CircleIcon size="8" fill="inherit" class="opacity-50 mr-1" />
            {{ post?.profile.time }}
          </span>
        </div>
      </div>
      <div class="py-4 text-body-1">
        {{ post?.data.content }}
      </div>
      <!---If Images-->
      <v-row v-if="post?.data.images">
        <v-col :md="photo.featured ? '12' : '6'" v-for="photo in post?.data.images">
          <v-avatar class="rounded-md w-100" size="360">
            <img :src="photo.img" alt="photo" />
          </v-avatar>
        </v-col>
      </v-row>
      <!--If Video-->
      <div v-if="post?.data.video" class="rounded-md overflow-hidden my-5">
        <iframe
          :src="`https://www.youtube.com/embed/${post?.data.video}`"
          frameborder="0"
          width="100%"
          height="400"
        ></iframe>
      </div>
      <!---Like and comment count-->
      <div class="my-4 mt-5 d-flex align-center gap-3">
        <v-tooltip text="Like">
          <template v-slot:activator="{ props }">
            <v-btn
              @click="handlePostLikes(`${post?.id}`)"
              v-bind="props"
              icon
              :color="
                post?.data && post?.data.likes && post?.data.likes.like ? 'primary' : 'grey100'
              "
              variant="flat"
              size="x-small"
            >
              <ThumbUpIcon size="16" />
            </v-btn>
          </template>
        </v-tooltip>
        <span class="text-subtitle-1 font-weight-semibold">{{
          post?.data && post?.data.likes && post?.data.likes.value
        }}</span>
        <v-tooltip text="Comment">
          <template v-slot:activator="{ props }">
            <v-btn
              icon
              v-bind="props"
              color="secondary"
              variant="flat"
              size="x-small"
              @click="toggleCommentbox"
            >
              <Message2Icon size="16" />
            </v-btn>
          </template>
        </v-tooltip>
        <span class="text-subtitle-1 font-weight-semibold">{{
          post?.data.comments ? post?.data.comments.length : 0
        }}</span>
        <v-menu>
          <template v-slot:activator="{ props }">
            <v-btn icon class="ml-auto" variant="text" size="small" v-bind="props"
              ><ShareIcon size="16"
            /></v-btn>
          </template>

          <v-list>
            <v-list-item v-for="(item, index) in shareitems" :key="index" :value="index">
              <template v-slot:prepend>
                <component :is="item.icon" stroke-width="1.5" size="20" />
              </template>
              <v-list-item-title class="ml-3">{{ item.title }}</v-list-item-title>
            </v-list-item>
          </v-list>
        </v-menu>
      </div>
    </v-card-item>
    <!--If Comment-->
    <div v-if="post?.data.comments" class="pa-5 pt-0">
      <div v-for="comments in post?.data.comments">
        <Comments :comments="comments" :postId="post?.id" />
      </div>
    </div>
    <!---Comment Form-->
    <div v-if="showCommentBox">
      <v-divider />
      <div class="d-block d-sm-flex gap-3 align-center mb-4 px-4 pt-4">
        <v-avatar class="flex-shrink-0 d-none d-sm-block" size="33">
          <img :src="post?.profile.avatar" width="33" alt="avatar" />
        </v-avatar>
        <v-text-field
          variant="outlined"
          color="primary"
          v-model="searchValue"
          placeholder="Comment"
          hide-details
        ></v-text-field>
        <v-btn
          color="primary"
          :disabled="searchValue === ''"
          class="mt-3 mt-sm-0"
          size="large"
          @click="onSubmit(post?.id, searchValue)"
        >
          Comment
        </v-btn>
      </div>
    </div>
  </v-card>
</template>
<script setup lang="ts">
import { ref, onMounted, computed } from 'vue';
import { usePostsStore } from '@/stores/apps/userprofile/posts';
import PostItem from './posts/PostItem.vue';

const store = usePostsStore();

onMounted(() => {
  store.fetchPosts();
});

const getPosts: any = computed(() => {
  return store.posts;
});
</script>

<template>
  <v-row class="mt-4">
    <v-col cols="12" v-for="post in getPosts" :key="post.id">
      <PostItem :post="post" />
    </v-col>
  </v-row>
</template>
<template>
  <v-card elevation="10" class="bg-surface">
    <v-card-item>
      <v-textarea
        elevation="10"
        color="primary"
        name="input-7-4"
        placeholder="Share your thoughts"
        rows="5"
      ></v-textarea>
      <div class="d-flex pb-2">
        <v-avatar size="32" color="primary" class="mr-2">
          <PhotoIcon size="16" />
        </v-avatar>
        <v-btn color="inherit" type="upload" variant="plain" class="opacity-1">
          Photo / Video
        </v-btn>
        <v-btn color="inherit" variant="plain" class="opacity-1">
          <v-avatar size="32" color="secondary" class="mr-2">
            <NotebookIcon size="16" />
          </v-avatar>
          Article
        </v-btn>
        <v-btn class="bg-primary ml-auto" size="large">Post</v-btn>
      </div>
    </v-card-item>
  </v-card>
</template>
