import { defineStore } from 'pinia';
// project imports
import axios from '@/utils/axios';
import { map } from 'lodash';

interface NotesType {
  id?: number | any;
  color?: string;
  title?: string;
  datef?: Date | any;
  deleted?: boolean;
}

interface noteType {
  notes: NotesType[];
  notesContent: number;
  noteSearch: string;
}

export const useNoteStore = defineStore({
  id: 'notes',
  state: (): noteType => ({
    notes: [],
    notesContent: 1,
    noteSearch: '',
  }),
  actions: {
    // Fetch notes
    async fetchNotes() {
      try {
        const data = await axios.get('/api/data/notes/NotesData');
        this.notes = data.data;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },

    //select chat
    SelectNote(itemID: number) {
      this.notesContent = itemID;
    },

    deleteNote(itemID: number) {
      const index = this.notes.findIndex((p) => p.id == itemID);
      this.notes.splice(index, 1);
    },
    updateNote(itemID: number, itemColor: any) {
      this.notes = map(this.notes, (note: any) => {
        if (note.id === itemID) {
          return {
            ...note,
            color: itemColor,
          };
        }
        return note;
      });
    },
  },
});
<script setup lang="ts">
import { ref, onMounted, computed } from 'vue';
import { useNoteStore } from '@/stores/apps/notes';
import { TrashIcon } from 'vue-tabler-icons';

const store = useNoteStore();

onMounted(() => {
  store.fetchNotes();
});

const getNotes = computed(() => {
  return store.notes;
});

const NoteItem = getNotes;

const searchValue = ref('');
const filteredNotes = computed(() => {
  return NoteItem.value.filter((note) => {
    return note.title?.toLowerCase().includes(searchValue.value.toLowerCase());
  });
});
</script>

<template>
  <!-- ---------------------------------------------------- -->
  <!-- Table Basic -->
  <!-- ---------------------------------------------------- -->
  <div class="pa-6">
    <h4 class="text-h6 mb-4 font-weight-semibold">All Notes</h4>

    <div class="mb-5">
      <v-text-field
        variant="outlined"
        v-model="searchValue"
        append-inner-icon="mdi-magnify"
        placeholder="Search Notes"
        hide-details
        density="compact"
      ></v-text-field>
    </div>

    <v-sheet
      :class="'note-sheet pa-6 pb-4 rounded-md cursor-pointer mb-4  bg-light' + note.color"
      v-for="note in filteredNotes"
      :key="note.id"
      @click="store.SelectNote(note.id)"
      v-if="filteredNotes.length > 0"
    >
      <h6 :class="'text-h6 text-truncate text-' + note.color">{{ note.title }}</h6>
      <div class="d-flex mt-3 align-center">
        <small class="text-subtitle-2 opacity-25">{{
          new Date(note.datef).toLocaleDateString()
        }}</small>
        <v-btn icon variant="text" class="ml-auto" size="x-small" @click="store.deleteNote(note.id)"
          ><v-tooltip activator="parent" location="top">Delete Note</v-tooltip><TrashIcon size="18"
        /></v-btn>
      </div>
    </v-sheet>
    <v-sheet v-if="filteredNotes.length === 0"
      ><v-alert
        type="error"
        title="Opps"
        text="The Notes you are looking for is not found"
      ></v-alert
    ></v-sheet>
  </div>
</template>
<style lang="scss">
.note-sheet {
  transition: 0.1s ease-in;
  &:hover {
    transform: scale(1.02);
  }
}
</style>
<script setup lang="ts">
import { ref, onMounted, computed } from 'vue';
import { useNoteStore } from '@/stores/apps/notes';
import { colorVariation } from '@/_mockApis/apps/notes/index';
import { uniqueId } from 'lodash';

// common components
const dialog = ref(false);
const title = ref('');
const color = ref('primary');
const store = useNoteStore();

onMounted(() => {
  store.fetchNotes();
});

const getNote = computed(() => {
  return store.notes;
});

function colorset(btcolor: any) {
  return (color.value = btcolor);
}
const getId = getNote.value.length;
function addNote() {
  return (
    getNote.value.push({
      id: getId + 1,
      title: title.value,
      color: color.value,
      datef: new Date(),
    }),
    (dialog.value = false),
    (title.value = '')
  );
}
</script>

<template>
  <!-- ---------------------------------------------------- -->
  <!-- Table Basic -->
  <!-- ---------------------------------------------------- -->

  <v-sheet>
    <v-btn color="primary" @click="dialog = true" rounded="pill">Add Notes</v-btn>

    <v-dialog v-model="dialog" max-width="500">
      <v-card>
        <v-card-text>
          <h4 class="text-h6 mb-4">Add Notes</h4>
          <v-textarea outlined name="Note" v-model="title"></v-textarea>
          <h4 class="text-h6 mt-4 mb-4">Select Notes Color</h4>
          <div class="d-flex gap-3 align-center">
            <v-btn
              icon
              v-for="btcolor in colorVariation"
              :key="btcolor.id"
              size="x-small"
              :color="btcolor.color"
              @click="colorset(btcolor.color)"
            >
              <CheckIcon width="16" v-if="color === btcolor.color" />
            </v-btn>
          </div>

          <div class="pt-6 pb-3 d-flex gap-2">
            <v-btn color="primary" @click="addNote" rounded="pill">Save</v-btn>
            <v-btn color="error" @click="dialog = false" rounded="pill">Close Dialog</v-btn>
          </div>
        </v-card-text>
      </v-card>
    </v-dialog>
  </v-sheet>
</template>
<script setup lang="ts">
import { ref, onMounted, computed } from 'vue';
import { useNoteStore } from '@/stores/apps/notes';
import { CheckIcon, Menu2Icon } from 'vue-tabler-icons';
import AddNote from './AddNote.vue';
import { colorVariation } from '@/_mockApis/apps/notes/index';

const store = useNoteStore();

onMounted(() => {
  store.fetchNotes();
});

const getNote = computed(() => {
  return store.notes[store.notesContent - 1];
});
// theme breadcrumb
</script>

<template>
  <!-- ---------------------------------------------------- -->
  <!-- Table Basic -->
  <!-- ---------------------------------------------------- -->

  <v-sheet>
    <v-sheet class="py-3 pl-6 pr-4 d-flex align-center">
      <h4 class="text-h6 font-weight-semibold">Edit Notes</h4>
      <div class="ml-auto"><AddNote /></div>
    </v-sheet>
    <v-divider></v-divider>
    <v-sheet v-if="getNote">
      <v-sheet class="pa-6">
        <h4 class="text-h6 mb-4">Change Title</h4>
        <v-textarea outlined name="Note" v-model="getNote.title"></v-textarea>

        <h4 class="text-h6 mt-4 mb-4">Change Notes Color</h4>
        <div class="d-flex gap-3 align-center">
          <v-btn
            icon
            v-for="btcolor in colorVariation"
            :key="btcolor.id"
            size="x-small"
            :color="btcolor.color"
            @click="store.updateNote(getNote.id, btcolor.color)"
          >
            <CheckIcon width="16" v-if="getNote.color === btcolor.color" />
          </v-btn>
        </div>
      </v-sheet>
    </v-sheet>
    <v-sheet v-else class="pa-6">
      <v-alert type="error" title="Opps" text="No Note selected Please select note"></v-alert
    ></v-sheet>
  </v-sheet>
</template>
