import { defineStore } from 'pinia';
// project imports
import axios from '@/utils/axios';
import { map } from 'lodash';
import { uniqueId } from 'lodash';
import { sub } from 'date-fns';
import type { Ref } from 'vue';

interface TaskType {
  id?: string | any;
  title?: string;
  subtitle?: string;
  datef?: Date | any;
  cardbg?: string;
  deleted?: boolean;
  taskimg?: string;
  date?: Date | any;
  category?: string;
  categorybg?: string;
  tasks?: TaskType[];
}

interface taskType {
  tasks: TaskType[];
  taskContent: string;
}

export const useTaskStore = defineStore({
  id: 'tasks',
  state: (): taskType => ({
    tasks: [],
    taskContent: '1',
  }),
  actions: {
    // Fetch tasks
    async fetchTasks() {
      try {
        const data = await axios.get('/api/data/task/TaskData');
        this.tasks = data.data;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },

    //select chat
    SelectTask(itemID: string) {
      this.taskContent = itemID;
    },

    deleteTask(itemID: string) {
      const taskObj = this.tasks.map((task) => {
        const tasks = task.tasks?.filter((t) => t.id !== itemID);
        return {
          ...task,
          tasks,
        };
      });
      this.tasks = taskObj;
    },
    addTask(
      columnId: string,
      title: string,
      subtitle: string,
      category: string,
      categorybg: string
    ) {
      const newTask = {
        id: uniqueId('#task_'),
        title: title,
        subtitle: subtitle,
        category: category,
        date: sub(new Date(), { seconds: 1 }),
        categorybg: categorybg,
      };
      this.tasks = map(this.tasks, (task: any) => {
        if (task.id === columnId) {
          return {
            ...task,
            ...task.tasks.push(newTask),
          };
        }
        return task;
      });
    },
  },
});
<script setup lang="ts">
import { ref } from 'vue';
import { useTaskStore } from '@/stores/apps/kanban/task';
import { formatDistanceToNowStrict } from 'date-fns';

const props = defineProps({
  task: null,
});

// common components
const dialog = ref(false);
const store = useTaskStore();

function save() {
  return (dialog.value = false);
}
function cancel() {
  return (dialog.value = false);
}
</script>

<template>
  <!-- ---------------------------------------------------- -->
  <!-- Table Basic -->
  <!-- ---------------------------------------------------- -->
  <v-card elevation="10" class="mb-5">
    <div class="d-flex align-center justify-space-between px-4 py-2 pr-3">
      <h5 class="text-subtitle-2 font-weight-semibold pr-4 cursor-move">{{ task?.title }}</h5>
      <NuxtLink to="" class="px-0 cursor-pointer">
        <DotsVerticalIcon size="15" />

        <v-menu activator="parent">
          <v-list density="compact">
            <v-list-item value="Edit">
              <v-list-item-title @click="dialog = true">Edit</v-list-item-title>
            </v-list-item>
            <v-list-item value="Delete">
              <v-list-item-title @click="store.deleteTask(task?.id)">Delete</v-list-item-title>
            </v-list-item>
          </v-list>
        </v-menu>
      </NuxtLink>
      <v-dialog v-model="dialog" max-width="500">
        <v-card>
          <v-card-text class="mb-4">
            <h4 class="text-h6 mb-5">Edit Task</h4>
            <v-text-field outlined name="Task" v-model="task.title" label="Title"></v-text-field>
            <v-textarea
              outlined
              name="Task-subtitle"
              v-model="task.subtitle"
              label="Subtitle"
            ></v-textarea>
            <v-select
              label="Select"
              v-model="task.category"
              variant="outlined"
              :items="['Mobile', 'Design', 'Development']"
            ></v-select>
            <v-select
              label="Select"
              v-model="task.categorybg"
              variant="outlined"
              :items="['primary', 'warning', 'success', 'error']"
            ></v-select>
            <v-btn color="primary" class="rounded-pill mr-2" variant="flat" @click="save"
              >Save</v-btn
            >
            <v-btn color="error" variant="flat" class="rounded-pill" @click="cancel">Cancel</v-btn>
          </v-card-text>
        </v-card>
      </v-dialog>
    </div>
    <!-- IF IMAGE -->
    <v-img
      v-if="task?.taskimg"
      :src="task?.taskimg"
      height="110px"
      cover
      class="w-100 mb-3"
    ></v-img>
    <!-- IF SUBTEXT -->
    <p v-if="task?.subtitle" class="text-subtitle-2 px-4">{{ task?.subtitle }}</p>

    <div class="d-flex align-center justify-space-between px-4 py-3">
      <div class="d-flex align-center">
        <CalendarIcon size="16" />
        <div class="body-text-1 text-dark pl-2">
          {{
            formatDistanceToNowStrict(new Date(task?.date), {
              addSuffix: false,
            })
          }}
          ago
        </div>
      </div>
      <div :class="'rounded-sm body-text-1 px-1 py-0 bg-' + task?.categorybg" size="small">
        {{ task?.category }}
      </div>
    </div>
  </v-card>
</template>
<script setup lang="ts">
import { ref } from 'vue';
import { useTaskStore } from '@/stores/apps/kanban/task';
import TaskItemCard from './TaskItemCard.vue';

const props = defineProps({
  column: Object,
});

// common components
const dialog = ref(false);
const store = useTaskStore();
const title = ref('');
const subtitle = ref('');
const category = ref('Mobile');
const categorybg = ref('primary');
const columnId = ref(props.column?.id);

function addItemAndClear() {
  (title.value = ''),
    (subtitle.value = ''),
    (category.value = 'Mobile'),
    (categorybg.value = 'primary');
}
</script>

<template>
  <v-card elevation="10" :class="'bg-' + column?.cardbg">
    <div class="pa-5">
      <div class="d-flex align-center justify-space-between">
        <h6 class="text-h6 font-weight-semibold">{{ column?.title }}</h6>

        <v-avatar
          size="22"
          elevation="10"
          class="bg-surface d-flex align-center cursor-pointer"
          @click="dialog = true"
        >
          <v-tooltip activator="parent" location="top">Add Task </v-tooltip>
          <PlusIcon size="13" stroke-width="2" />
        </v-avatar>
      </div>
      <draggable
        class="dragArea list-group mt-6"
        :list="column?.tasks"
        :animation="200"
        ghost-class="ghost-card"
        group="tasks"
      >
        <transition-group>
          <div v-for="task in column?.tasks" :key="task.id" :task="task" class="mt-3 cursor-move">
            <TaskItemCard :task="task" />
          </div>
        </transition-group>
      </draggable>
    </div>
    <v-dialog v-model="dialog" max-width="500">
      <v-card>
        <v-card-text class="mb-4">
          <h4 class="text-h6 mb-5">Add Task</h4>
          <form
            @submit.prevent="
              (store.addTask(columnId, title, subtitle, category, categorybg), addItemAndClear())
            "
          >
            <v-text-field outlined name="Task" v-model="title" label="Title"></v-text-field>
            <v-textarea
              outlined
              name="Task-subtitle"
              v-model="subtitle"
              label="Subtitle"
            ></v-textarea>
            <v-select
              label="Select"
              v-model="category"
              variant="outlined"
              :items="['Mobile', 'Design', 'Development']"
            ></v-select>
            <v-select
              label="Select"
              v-model="categorybg"
              variant="outlined"
              :items="['primary', 'warning', 'success', 'error']"
            ></v-select>
            <v-btn
              color="primary"
              class="rounded-pill"
              variant="flat"
              type="submit"
              :disabled="title == ''"
              @click="dialog = false"
              >Save</v-btn
            >
          </form>
        </v-card-text>
      </v-card>
    </v-dialog>
  </v-card>
</template>
<script lang="ts" setup>
import { ref, onMounted, computed } from 'vue';
// common components
import TaskColumn from './TaskColumn.vue';
import { useTaskStore } from '@/stores/apps/kanban/task';

const store = useTaskStore();

onMounted(() => {
  store.fetchTasks();
});

const getTasks = computed(() => {
  return store.tasks;
});

const columns = getTasks;
</script>

<template>
  <v-card elevation="10">
    <div class="pa-5">
      <h5 class="text-h5 font-weight-semibold mb-7">Kanban Application</h5>
      <v-row>
        <v-col cols="12" md="3" sm="6" class="d-flex" v-for="column in columns" :key="column.id">
          <TaskColumn :column="column" />
        </v-col>
      </v-row>
    </div>
  </v-card>
</template>
