import { defineStore } from 'pinia';
import axios from '@/utils/axios';
import { uniqueId } from 'lodash';
import { sub } from 'date-fns';

interface EmailType {
  emails: any[];
  selectedEmail: any | null; // Add this line to hold the selected email
}

export const useEmailStore = defineStore({
  id: 'email',
  state: (): EmailType => ({
    emails: [],
    selectedEmail: null, // Initialize as null
  }),
  actions: {
    toggleStarred(emailId: number) {
      const email = this.emails.find((e) => e.id === emailId);
      if (email) {
        email.starred = !email.starred;
      }
    },
    toggleImportant(emailId: number) {
      const email = this.emails.find((e) => e.id === emailId);
      if (email) {
        email.important = !email.important;
      }
    },
    async fetchEmails() {
      try {
        const data = await axios.get('/api/data/email/EmailData');
        this.emails = data.data;
      } catch (error) {
        alert(error);
        console.log(error);
      }
    },
    selectEmail(email: any) {
      // Update the method to accept an email object
      this.selectedEmail = email; // Store the selected email
    },
    deleteEmail(id: number) {
      this.emails = this.emails.filter((email) => email.id !== id);
      this.selectedEmail = null; // Clear selected email after deletion
    },
  },
});
<script setup lang="ts">
import { ref } from 'vue';
import { useDisplay } from 'vuetify';
const { xs, lgAndUp } = useDisplay();

const sDrawer = ref(false); // Sidebar drawer for mobile
const eDrawer = ref(false); // Email details drawer
const selectedEmail = ref(null); // To store the selected email details

// Method to select an email and open the email details drawer
const openEmailDetails = (email: any) => {
  selectedEmail.value = email; // Store the selected email
  eDrawer.value = true; // Open the email details drawer
};
</script>

<template>
  <div class="d-flex mainbox">
    <div class="compose pa-6" v-if="lgAndUp">
      <slot name="mailCompose"></slot>
    </div>

    <div class="mail-list">
      <v-divider class="d-lg-none d-block" />
      <slot name="mailList" :openEmailDetails="openEmailDetails"></slot>
      <!-- Pass the method as a slot prop -->
    </div>

    <div class="mail-details pa-6 d-md-block d-none">
      <slot name="mailDetail"></slot>
    </div>
  </div>
</template>

<style lang="scss">
.compose {
  max-width: 235px;
  width: 100%;
}
.mail-list {
  max-width: 340px;
  width: 100%;
}
.mail-details {
  width: 100%;
}

@media screen and (max-width: 991px) {
  .mail-list {
    max-width: 100%;
  }
}
@media screen and (min-width: 991px) {
  .compose,
  .mail-list {
    border-inline-end-width: thin !important;
    border-inline-end-style: solid !important;
    border-inline-end-color: rgba(var(--v-border-color), var(--v-border-opacity)) !important;
  }
}
</style>
<script setup lang="ts">
import { ref, onMounted } from 'vue';
import { Icon } from '@iconify/vue';

interface FilterType {
  id?: number;
  filterbyTitle?: string;
  icon?: any;
  name: string;
  divider?: boolean;
  color?: string;
}

const filterData1: FilterType[] = [
  { id: 2, name: 'inbox', icon: 'solar:inbox-line-broken' },
  { id: 3, name: 'sent', icon: 'solar:plain-2-linear' },
  { id: 4, name: 'draft', icon: 'solar:notes-linear' },
  { id: 5, name: 'spam', icon: 'solar:flag-2-outline' },
  { id: 6, name: 'trash', icon: 'solar:trash-bin-trash-outline' },
  {
    id: 7,
    name: 'starred',
    icon: 'solar:star-fall-minimalistic-2-line-duotone',
  },
  {
    id: 8,
    name: 'important',
    icon: 'solar:bell-bing-linear',
  },
  {
    id: 10,
    name: 'promotional',
    icon: 'solar:folder-open-linear',
    color: 'primary',
  },
  {
    id: 11,
    name: 'social',
    icon: 'solar:folder-open-linear',
    color: 'error',
  },
  {
    id: 12,
    name: 'health',
    icon: 'solar:folder-open-linear',
    color: 'success',
  },
];

const selectedFilter = ref<string | null>('inbox');

const emit = defineEmits(['filter-change']);
const handleFilterClick = (filterName: string) => {
  selectedFilter.value = filterName;
  emit('filter-change', filterName);
};

onMounted(() => {
  handleFilterClick('inbox');
});

const showComposeEmail = ref(false);
</script>

<template>
  <div>
    <v-btn color="primary" class="w-100 rounded-pill" @click="showComposeEmail = true"
      >compose</v-btn
    >
    <!-- Sidebar Filter Lists -->
    <v-list class="my-4 theme-list">
      <v-list-item
        v-for="item in filterData1.slice(0, 5)"
        :key="item.id"
        :value="item"
        color="primary"
        rounded="md"
        class="mail-items"
        :class="{
          'active bg-light text-primary': selectedFilter === item.name,
        }"
        @click="handleFilterClick(item.name)"
      >
        <template v-slot:prepend>
          <Icon :icon="item.icon" height="18" />
        </template>
        <v-list-item-title class="ms-3 text-capitalize">{{ item.name }}</v-list-item-title>
      </v-list-item>

      <v-list-item-title class="mt-4 mb-2 border-t pt-5 text-uppercase text-12 font-weight-semibold"
        >Sort By</v-list-item-title
      >

      <v-list-item
        v-for="item in filterData1.slice(5, 7)"
        :key="item.id"
        :value="item"
        color="primary"
        rounded="md"
        class="mail-items"
        :class="{
          'active bg-light text-primary': selectedFilter === item.name,
        }"
        @click="handleFilterClick(item.name)"
      >
        <template v-slot:prepend>
          <Icon :icon="item.icon" height="18" />
        </template>
        <v-list-item-title class="ms-3 text-capitalize">{{ item.name }}</v-list-item-title>
      </v-list-item>

      <v-list-item-title class="mt-4 mb-2 border-t pt-5 text-uppercase text-12 font-weight-semibold"
        >Labels</v-list-item-title
      >

      <v-list-item
        v-for="item in filterData1.slice(7)"
        :key="item.id"
        :value="item"
        color="primary"
        rounded="md"
        class="mail-items"
        :class="{
          'active bg-bglight text-primary': selectedFilter === item.name,
        }"
        @click="handleFilterClick(item.name)"
      >
        <template v-slot:prepend>
          <Icon :icon="item.icon" height="18" :class="'text-' + item.color" />
        </template>
        <v-list-item-title class="ms-3 text-capitalize">{{ item.name }}</v-list-item-title>
      </v-list-item>
    </v-list>
  </div>

  <v-dialog v-model="showComposeEmail" max-width="500px">
    <v-card>
      <v-card-title class="pa-4 bg-primary">Compose Mail</v-card-title>
      <v-card-text>
        <v-form>
          <v-text-field label="To" required></v-text-field>
          <v-text-field label="Subject" required></v-text-field>
          <v-textarea label="Message" rows="4" required></v-textarea>
          <v-file-input
            prepend-icon="null"
            append-icon="null"
            label="Attachment"
            variant="outlined"
            hide-details
            class="w-100 mx-0 no-icon"
          >
          </v-file-input>
        </v-form>
      </v-card-text>
      <v-card-actions class="ga-3">
        <v-btn
          color="error"
          rounded="pill"
          variant="flat"
          class="px-5"
          @click="showComposeEmail = false"
          >Cancel</v-btn
        >
        <v-btn color="primary" rounded="pill" class="px-5" variant="flat">Send</v-btn>
      </v-card-actions>
    </v-card>
  </v-dialog>
</template>
<script setup lang="ts">
import { ref, computed, watch, onMounted } from 'vue';
import { useEmailStore } from '@/stores/apps/email'; // Import the store
import type { EmailType } from '@/types/apps/EmailTypes';
import { Icon } from '@iconify/vue';
import { format } from 'date-fns';

interface Props {
  emails: EmailType[];
}

const props = defineProps<Props>();
const store = useEmailStore();

const selectedEmail = ref<EmailType | null>(null);
const selectedEmails = ref<EmailType[]>([]);
const searchTerm = ref('');

// Computed property to filter emails by name
const filteredEmails = computed(() => {
  return props.emails.filter((email) =>
    email.from.toLowerCase().includes(searchTerm.value.toLowerCase())
  );
});

// Function to select an email
const selectEmail = (email: EmailType) => {
  selectedEmail.value = email;
  store.selectEmail(email);
};

const toggleEmailSelection = (email: EmailType) => {
  const index = selectedEmails.value.findIndex((e) => e.id === email.id);
  if (index === -1) {
    selectedEmails.value.push(email);
  } else {
    selectedEmails.value.splice(index, 1);
  }
};

// Function to delete selected emails
const deleteSelectedEmails = (email: EmailType) => {
  store.deleteEmail(email.id);
  selectedEmails.value = selectedEmails.value.filter((e) => e.id !== email.id);
};

const isAnyEmailSelected = computed(() => selectedEmails.value.length > 0);
onMounted(() => {
  if (props.emails.length > 0) {
    selectedEmail.value = props.emails[0];
    store.selectEmail(props.emails[0]);
  }
});

watch(
  () => props.emails,
  (newEmails) => {
    if (newEmails.length > 0 && !selectedEmail.value) {
      selectedEmail.value = newEmails[0];
      store.selectEmail(newEmails[0]);
    }
  },
  { immediate: true }
);

const toggleIcon = (email: EmailType): void => {
  store.toggleStarred(email.id); // Toggle the starred status
};

const toggleIconimportant = (email: EmailType): void => {
  store.toggleImportant(email.id); // Toggle the starred status
};
</script>

<template>
  <div>
    <!-- Search input -->
    <v-text-field
      v-model="searchTerm"
      label="Search Emails"
      variant="outlined"
      hide-details
      class="w-100 mb-1 pa-6"
      density="compact"
      @click.stop
    >
      <template v-slot:prepend-inner>
        <Icon icon="solar:magnifer-linear" height="18" width="25" />
      </template>
    </v-text-field>

    <!-- Email list -->
    <perfect-scrollbar class="max-h-600">
      <div v-if="filteredEmails.length">
        <div
          v-for="email in filteredEmails"
          :key="email.id"
          :class="[
            'email-items cursor-pointer',
            { 'selected-email bg-bglight': email === selectedEmail },
          ]"
          @click="selectEmail(email)"
        >
          <div class="d-flex align-center justify-space-between">
            <div class="d-flex align-center">
              <v-checkbox
                hide-details
                color="primary"
                class="ms-n2"
                :model-value="selectedEmails.includes(email)"
                @change="toggleEmailSelection(email)"
              ></v-checkbox>
              <h6 class="text-14 font-weight-semibold email-title">
                {{ email.from }}
              </h6>
            </div>
            <v-chip
              class="text-12"
              :color="
                email.label === 'Promotional'
                  ? 'primary'
                  : email.label === 'Social'
                    ? 'error'
                    : email.label === 'Health'
                      ? 'success'
                      : 'secondary'
              "
              variant="flat"
              size="small"
              rounded="pill"
              label
            >
              {{ email.label }}
            </v-chip>
          </div>
          <div class="ps-8 mt-n1">
            <p class="text-truncate text-14">{{ email.subject }}</p>
            <div class="d-flex align-center justify-space-between mt-3">
              <div class="d-flex align-center ga-2">
                <!-- Toggle icon for each email based on its id -->
                <button @click.stop="toggleIcon(email)">
                  <icon
                    v-if="email.starred"
                    icon="solar:star-bold"
                    height="15"
                    class="text-warning"
                  />
                  <icon v-else icon="solar:star-line-duotone" height="15" />
                </button>

                <button @click.stop="toggleIconimportant(email)">
                  <icon
                    v-if="email.important"
                    icon="solar:info-circle-bold"
                    height="15"
                    class="text-info"
                  />
                  <icon v-else icon="solar:info-circle-outline" height="15" />
                </button>
                <button @click.stop="deleteSelectedEmails(email)">
                  <icon
                    icon="solar:trash-bin-trash-outline"
                    height="15"
                    v-if="selectedEmails.includes(email)"
                    class="delete-icon"
                  />
                </button>
              </div>
              <small class="text-muted">
                {{ email.time ? format(new Date(email.time), 'E, MMM d') : 'N/A' }}
              </small>
            </div>
          </div>
        </div>
      </div>
      <div v-else>
        <div class="px-6">
          <v-alert color="warning" variant="tonal" class="mb-4">
            <template v-slot:prepend>
              <v-icon class="text-24">mdi-alert-circle-outline</v-icon>
            </template>
            <div>No Email found for this search</div>
          </v-alert>
        </div>
      </div>
    </perfect-scrollbar>
  </div>
</template>
import { publicAssetsURL } from '#internal/nuxt/paths';export default publicAssetsURL("/images/backgrounds/empty_mail.png")
