mock.onGet('/api/address/list').reply(() => {
  return [200, address];
});

mock.onPost('/api/address/new').reply((request: any) => {
  try {
    const data = JSON.parse(request.data);
    const { name, destination, building, street, city, state, country, post, phone, isDefault } =
      data;
    const newAddress = {
      id: new Date(),
      name,
      destination,
      building,
      street,
      city,
      state,
      country,
      post,
      phone,
      isDefault,
    };

    if (isDefault) {
      address = address.map((item) => {
        if (item.isDefault === true) {
          return { ...item, isDefault: false };
        }
        return item;
      });
    }

    address = [...address, newAddress];

    return [200, { address }];
  } catch (err) {
    console.error(err);
    return [500, { message: 'Internal server error' }];
  }
});

mock.onPost('/api/address/edit').reply((request: any) => {
  try {
    const data = JSON.parse(request.data);

    if (data.isDefault) {
      address = address.map((item) => {
        if (item.isDefault === true) {
          return { ...item, isDefault: false };
        }
        return item;
      });
    }

    address = address.map((item) => {
      if (item.id === data.id) {
        return data;
      }
      return item;
    });

    return [200, { address }];
  } catch (err) {
    console.error(err);
    return [500, { message: 'Internal server error' }];
  }
});

